import React, { useState, useEffect } from 'react';
import axios from 'axios';

const StudentDashboard = () => {
    const [user, setUser] = useState(null);
    const [isEditing, setIsEditing] = useState(false);
    const [formData, setFormData] = useState({});

    useEffect(() => {
        const fetchProfile = async () => {
            try {
                const token = localStorage.getItem('token');
                // The backend API will use the token to identify the user
                const res = await axios.get('http://localhost:5000/api/students/profile', {
                    headers: { 'x-auth-token': token },
                });
                setUser(res.data);
                setFormData(res.data);
            } catch (err) {
                console.error(err.response.data.msg);
            }
        };
        fetchProfile();
    }, []);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleUpdate = async () => {
        try {
            const token = localStorage.getItem('token');
            await axios.put('http://localhost:5000/api/students/profile', formData, {
                headers: { 'x-auth-token': token },
            });
            setIsEditing(false);
            setUser(formData);
        } catch (err) {
            console.error(err.response.data.msg);
        }
    };

    if (!user) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h2>Student Dashboard</h2>
            {isEditing ? (
                <div>
                    <input type="text" name="name" value={formData.name} onChange={handleChange} />
                    <input type="email" name="email" value={formData.email} onChange={handleChange} />
                    <input type="text" name="course" value={formData.course} onChange={handleChange} />
                    <button onClick={handleUpdate}>Save</button>
                </div>
            ) : (
                <div>
                    <p>Name: {user.name}</p>
                    <p>Email: {user.email}</p>
                    <p>Course: {user.course}</p>
                    <p>Enrollment Date: {new Date(user.enrollmentDate).toLocaleDateString()}</p>
                    <button onClick={() => setIsEditing(true)}>Edit Profile</button>
                </div>
            )}
        </div>
    );
};

export default StudentDashboard;
